import React, { Component, Fragment }  from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

export default class AdminSinhvien extends Component {

  state = {
    isEditMode: false,
    updatedproductname: this.props.name,
    updatedproductdate: this.props.date    
  }

  handleProductEdit = event => {
    event.preventDefault();
    this.setState({ isEditMode: true });
  }

  handleEditSave = event => {
    event.preventDefault();
    this.setState({ isEditMode: false });
    this.props.handleUpdateSinhvien(this.props.mssv, this.state.updatedproductname, this.state.updatedproductdate);
  }

  onAddProductNameChange = event => this.setState({ "updatedproductname": event.target.value });
  onAddProductDateChange = event => this.setState({ "updatedproductdate": event.target.value });

  render() {
    return (
      <div className="tile is-child box notification is-success">
        {
          this.props.isAdmin && 
          <Fragment>
            <a href="/" onClick={this.handleProductEdit} className="product-edit-icon">
              <FontAwesomeIcon icon="edit" />
            </a>
            <button onClick={event => this.props.handleDeleteProduct(this.props.mssv, event)} className="delete"></button>
          </Fragment>
        }
        {
          this.state.isEditMode 
          ? <div>
              <p>Edit product name</p>
              <input 
                className="input is-medium"
                type="text" 
                placeholder="Enter name"
                value={this.state.updatedproductname}
                onChange={this.onAddProductNameChange}
              />
              <input 
                className="input is-medium2"
                type="text" 
                placeholder="Enter date"
                value={this.state.updatedproductdate}
                onChange={this.onAddProductDateChange}
              />
              <p className="product-id">mssv: { this.props.mssv }</p>
              <button type="submit" 
                className="button is-info is-small"
                onClick={ this.handleEditSave }
              >save</button>
            </div>
          : <div>
              <p className="product-title">name: { this.props.name }</p>
              <p className="product-title">date: { this.props.date }</p>
              <p className="product-title">mssv: { this.props.mssv }</p>
            </div>
        }
      </div>
    )
  }
}
