import React, { Component, Fragment }  from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

export default class AdminSinhvien extends Component {

  state = {
    isEditMode: false,
    updatedsinhvienname: this.props.name,
    updatedsinhviendate: this.props.date,
    updatedsinhviensex: this.props.sex   
  }

  handleSinhvienEdit = event => {
    event.preventDefault();
    this.setState({ isEditMode: true });
  }

  handleEditSave = event => {
    event.preventDefault();
    this.setState({ isEditMode: false });
    this.props.handleUpdateSinhvien(this.props.mssv, this.state.updatedsinhvienname, this.state.updatedsinhviendate, this.state.updatedsinhviensex);
  }

  onAddSinhvienNameChange = event => this.setState({ "updatesinhvienname": event.target.value });
  onAddProductDateChange = event => this.setState({ "updatedsinhviendate": event.target.value });
  onAddSinhvienSexChange = event => this.setState({ "updatedsinhviensex": event.target.value });

  render() {
    return (
      <div className="tile is-child box notification is-success">
        {
          this.props.isAdmin && 
          <Fragment>
            <a href="/" onClick={this.handleSinhvienEdit} className="product-edit-icon">
              <FontAwesomeIcon icon="edit" />
            </a>
            <button onClick={event => this.props.handleDeleteProduct(this.props.mssv, event)} className="delete"></button>
          </Fragment>
        }
        {
          this.state.isEditMode 
          ? <div>
              <p>Edit product name</p>
              <input 
                className="input is-medium"
                type="text" 
                placeholder="Enter name"
                value={this.state.updatedsinhvienname}
                onChange={this.onAddSinhvienNameChange}
              />
              <input 
                className="input is-medium"
                type="text" 
                placeholder="Enter date"
                value={this.state.updatedsinhviendate}
                onChange={this.onAddProductDateChange}
              />
              <input 
                className="input is-medium"
                type="text" 
                placeholder="Enter sex"
                value={this.state.updatedsinhviensex}
                onChange={this.onAddSinhvienSexChange}
              />
              <p className="product-id">mssv: { this.props.mssv }</p>
              <button type="submit" 
                className="button is-info is-small"
                onClick={ this.handleEditSave }
              >save</button>
            </div>
          : <div>
              <p className="sinhvien-title">name: { this.props.name }</p>
              <p className="sinhvien-title">date: { this.props.date }</p>
              <p className="sinhvien-title">sex: { this.props.sex }</p>
              <p className="sinhvien-title">mssv: { this.props.mssv }</p>
            </div>
        }
      </div>
    )
  }
}
