import React, { Component, Fragment }  from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

export default class ProductAdmin extends Component {

  state = {
    isEditMode: false,
    updatedproductstudentname: this.props._studentname,
    updatedproductbirthday: this.props._birthday,
    updatedproductsex: this.props.sex,
    updatedproductclass: this.props._class,
    updatedproductphone: this.props._phone,
    updatedproductaddress: this.props.address
  }

  handleProductEdit = event => {
    event.preventDefault();
    this.setState({ isEditMode: true });
  }

  handleEditSave = event => {
    event.preventDefault();
    this.setState({ isEditMode: false });
    // this.props.handleUpdateProduct(this.props.id, this.state.updatedproductstudentname);
    // this.props.handleUpdateProduct(this.props.id, this.state.updatedproductbirthday);
    // this.props.handleUpdateProduct(this.props.id, this.state.updatedproductsex);
    // this.props.handleUpdateProduct(this.props.id, this.state.updatedproductclass);
    // this.props.handleUpdateProduct(this.props.id, this.state.updatedproductphone);
    this.props.handleUpdateProduct(this.props.address, this.state.updatedproductaddress);
  }

  onAddProductNameChange = event => this.setState({ "updatedproductstudentname": event.target.value });
  onAddProductBirthDayChange = event => this.setState({ "updatedproductbirthday": event.target.value });
  onAddProductSexChange = event => this.setState({ "updatedproductsex": event.target.value });
  onAddProductClassChange = event => this.setState({ "updatedproductclass": event.target.value });
  onAddProductPhoneChange = event => this.setState({ "updatedproductphone": event.target.value });
  onAddProductAddressChange = event => this.setState({ "updatedproductaddress": event.target.value });


  render() {
    return (
      <div className="tile is-child box notification is-success">
        {
          this.props.isAdmin && 
          <Fragment>
            <a href="/" onClick={this.handleProductEdit} className="product-edit-icon">
              <FontAwesomeIcon icon="edit" />
            </a>
            <button onClick={event => this.props.handleDeleteProduct(this.props.id, event)} className="delete"></button>
          </Fragment>
        }
        {
          this.state.isEditMode 
          ? <div>
              <p>Edit product name</p>
              <p className="product-id">id: { this.props.id }</p>

              <input 
                className="input is-medium"
                type="text" 
                placeholder="Enter name"
                value={this.state.updatedproductstudentname}
                onChange={this.onAddProductNameChange}
              />
              <p className="product-title">name: { this.props._studentname }</p>
              
              <input 
                className="input is-medium"
                type="text" 
                placeholder="Enter birthday"
                value={this.state.updatedproductbirthday}
                onChange={this.onAddProductBirthDayChange}
              />
              <p className="product-title">birthday: { this.props._birthday }</p>

              <input 
                className="input is-medium"
                type="text" 
                placeholder="Enter sex"
                value={this.state.updatedproductsex}
                onChange={this.onAddProductSexChange}
              />
              <p className="product-title">sex: { this.props.sex }</p>

              <input 
                className="input is-medium"
                type="text" 
                placeholder="Enter class"
                value={this.state.updatedproductclass}
                onChange={this.onAddProductClassChange}
              />
              <p className="product-title">class: { this.props._class }</p>

              <input 
                className="input is-medium"
                type="text" 
                placeholder="Enter phone"
                value={this.state.updatedproductphone}
                onChange={this.onAddProductPhoneChange}
              />
              <p className="product-title">phone: { this.props._phone }</p>

              <input 
                className="input is-medium"
                type="text" 
                placeholder="Enter address"
                value={this.state.updatedproductaddress}
                onChange={this.onAddProductAddressChange}
              />
              <p className="product-title">address: { this.props.address }</p>

              <button type="submit" 
                className="button is-info is-small"
                onClick={ this.handleEditSave }
              >save</button>
            </div>
          : <div>
              <p className="product-id">id: { this.props.id }</p>
              <p className="product-title">name:{ this.props._studentname }</p>
              <p className="product-title">birthday: { this.props._birthday }</p>
              <p className="product-title">sex: { this.props.sex }</p>
              <p className="product-title">class: { this.props._class }</p>
              <p className="product-title">phone: { this.props._phone }</p>
              <p className="product-title">address: { this.props.address }</p>
            </div>
        }
      </div>
    )
  }
}
