import React, { Component, Fragment } from 'react';
import Product from './Product';
import axios from "axios";
const config = require('../config.json');

export default class ProductAdmin extends Component {

  state = {
    newproduct: { 
      "id": "",
      "studentname": "", 
      "birthday": "",
      "sex": "",
      "class": "",
      "phone": "",
      "address": ""
    },
    products: []
  }

  handleAddProduct = (id, event) => {
    event.preventDefault();
    // add call to AWS API Gateway add product endpoint here
    this.setState({ products: [...this.state.products, this.state.newproduct] })
    this.setState({ 
      newproduct: { 
        "id": "",
        "studentname": "", 
        "birthday": "",
        "sex": "",
        "class": "",
        "phone": "",
        "address": ""
    }});
  }

  handleUpdateProduct = (id, studentname, birthday, sex, _class, phone, address) => {
    // add call to AWS API Gateway update product endpoint here
    const productToUpdate = [...this.state.products].find(product => product.id === id);
    const updatedProducts = [...this.state.products].filter(product => product.id !== id);
    productToUpdate.studentname = _studentname;
    productToUpdate.birthday = _birthday;
    productToUpdate.sex = sex;
    productToUpdate.class = _class;
    productToUpdate.phone = _phone;
    productToUpdate.address = address;
    updatedProducts.push(productToUpdate);
    this.setState({products: updatedProducts});
  }

  handleDeleteProduct = (id, event) => {
    event.preventDefault();
    // add call to AWS API Gateway delete product endpoint here
    const updatedProducts = [...this.state.products].filter(product => product.id !== id);
    this.setState({products: updatedProducts});
  }

  fetchProducts = () => {
    // add call to AWS API Gateway to fetch products here
    // then set them in state
  }

  onAddProductIdChange = event => this.setState({ newproduct: { ...this.state.newproduct, "id": event.target.value } });
  onAddProductNameChange = event => this.setState({ newproduct: { ...this.state.newproduct, "studentname": event.target.value } });
  onAddProductBirthDayChange = event => this.setState({ newproduct: { ...this.state.newproduct, "birthday": event.target.value } });  
  onAddProductSexChange = event => this.setState({ newproduct: { ...this.state.newproduct, "sex": event.target.value } });
  onAddProductClassChange = event => this.setState({ newproduct: { ...this.state.newproduct, "class": event.target.value } });
  onAddProductPhoneChange = event => this.setState({ newproduct: { ...this.state.newproduct, "phone": event.target.value } });
  onAddProductAddressChange = event => this.setState({ newproduct: { ...this.state.newproduct, "address": event.target.value } });
  componentDidMount = () => {
    this.fetchProducts();
  }

  render() {
    return (
      <Fragment>
        <section className="section">
          <div className="container">
            <h1>Product Admin</h1>
            <p className="subtitle is-5">Add and remove products using the form below:</p>
            <br />
            <div className="columns">
              <div className="column is-one-third">
                <form onSubmit={event => this.handleAddProduct(this.state.newproduct.id, event)}>
                  <div className="field has-addons">
                    <div className="control">
                    <div className="control">
                      <input 
                        className="input is-medium"
                        type="text" 
                        placeholder="Enter id"
                        value={this.state.newproduct.id}
                        onChange={this.onAddProductIdChange}
                      />
                    </div>
                      <input 
                        className="input is-medium"
                        type="text" 
                        placeholder="Enter name"
                        value={this.state.newproduct.studentname}
                        onChange={this.onAddProductNameChange}
                      />
                    </div>
                    <div className="control">
                      <input 
                        className="input is-medium"
                        type="text" 
                        placeholder="Enter birthday"
                        value={this.state.newproduct.birthday}
                        onChange={this.onAddProductBirthDayChange}
                      />
                    </div>
                    <div className="control">
                      <input 
                        className="input is-medium"
                        type="text" 
                        placeholder="Enter sex"
                        value={this.state.newproduct.sex}
                        onChange={this.onAddProductSexChange}
                      />
                    </div>
                    <div className="control">
                      <input 
                        className="input is-medium"
                        type="text" 
                        placeholder="Enter class"
                        value={this.state.newproduct.class}
                        onChange={this.onAddProductClassChange}
                      />
                    </div>
                    <div className="control">
                      <input 
                        className="input is-medium"
                        type="text" 
                        placeholder="Enter phone"
                        value={this.state.newproduct.phone}
                        onChange={this.onAddProductPhoneChange}
                      />
                    </div>
                    <div className="control">
                      <input 
                        className="input is-medium"
                        type="text" 
                        placeholder="Enter address"
                        value={this.state.newproduct.address}
                        onChange={this.onAddProductAddressChange}
                      />
                    </div>
                    <div className="control">
                      <button type="submit" className="button is-primary is-medium">
                        Add product
                      </button>
                    </div>
                  </div>
                </form>
              </div>
              <div className="column is-two-thirds">
                <div className="tile is-ancestor">
                  <div className="tile is-4 is-parent  is-vertical">
                    { 
                      this.state.products.map((product, index) => 
                        <Product 
                          isAdmin={true}
                          handleUpdateProduct={this.handleUpdateProduct}
                          handleDeleteProduct={this.handleDeleteProduct} 
                          id={product.id}
                          _studentname={product.studentname} 
                          _birthday={product.birthday}
                          sex={product.sex}
                          _class={product.class}
                          _phone={product.phone}                        
                          address={product.address}
                          key={product.id}
                        />)
                    }
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </Fragment>
    )
  }
}
