import React, { Component, Fragment } from 'react';
import Sinhvien from './Sinhvien';
import axios from "axios";
const config = require('../config.json');

export default class ProductAdmin extends Component {

  state = {
    newsinhvien: { 
      "productname": "", 
      "sinhviendate": "",
      "sinhviensex": "",
      "mssv": ""
    },
    sinhviens: []
  }

  handleAddSinhvien = (mssv, event) => {
    event.preventDefault();
    // add call to AWS API Gateway add product endpoint here
    this.setState({ sinhviens: [...this.state.sinhviens, this.state.newsinhvien] })
    this.setState({ newsinhvien: { "productname": "","sinhviendate": "","sinhviensex": "" ,"mssv": ""}});
  }

  handleUpdateSinhvien = (mssv, name, date,sex) => {
    // add call to AWS API Gateway update product endpoint here
    const productToUpdate = [...this.state.sinhviens].find(product => product.mssv === mssv);
    const updatedProducts = [...this.state.sinhviens].filter(product => product.mssv !== mssv);
    productToUpdate.productname = name;
    productToUpdate.sinhviendate = date;
    productToUpdate.sinhviensex = sex;
    updatedProducts.push(productToUpdate);
    this.setState({sinhviens: updatedProducts});
  }

  handleDeleteProduct = (mssv, event) => {
    event.preventDefault();
    // add call to AWS API Gateway delete product endpoint here
    const updatedProducts = [...this.state.sinhviens].filter(product => product.mssv !== mssv);
    this.setState({sinhviens: updatedProducts});
  }

  fetchProducts = () => {
    // add call to AWS API Gateway to fetch products here
    // then set them in state
  }

  onAddProductNameChange = event => this.setState({ newsinhvien: { ...this.state.newsinhvien, "productname": event.target.value } });
  onAddProductDateChange = event => this.setState({ newsinhvien: { ...this.state.newsinhvien, "sinhviendate": event.target.value} });
  onAddProductSexChange = event => this.setState({ newsinhvien: { ...this.state.newsinhvien, "sinhviensex": event.target.value} });
  onAddProductIdChange = event => this.setState({ newsinhvien: { ...this.state.newsinhvien, "mssv": event.target.value } });
  

  componentDidMount = () => {
    this.fetchProducts();
  }

  render() {
    return (
      <Fragment>
        <section className="section">
          <div className="container">
            <h1>Product Admin</h1>
            <p className="subtitle is-5">Add and remove products using the form below:</p>
            <br />
            <div className="columns">
              <div className="column is-one-third">
                <form onSubmit={event => this.handleAddSinhvien(this.state.newsinhvien.mssv, event)}>
                  <div className="field has-addons">
                    <div className="control">
                      <input 
                        className="input is-medium"
                        type="text" 
                        placeholder="Enter name"
                        value={this.state.newsinhvien.productname}
                        onChange={this.onAddProductNameChange}
                      />
                      <input 
                        className="input is-medium"
                        type="text" 
                        placeholder="Enter name"
                        value={this.state.newsinhvien.sinhviendate}
                        onChange={this.onAddProductDateChange}
                      />
                      <input 
                        className="input is-medium"
                        type="text" 
                        placeholder="Enter sex"
                        value={this.state.newsinhvien.sinhviensex}
                        onChange={this.onAddProductSexChange}
                      />
                    </div>
                    {/* <div className="control">
                      
                    </div> */}
                    <div className="control">
                      <input 
                        className="input is-medium"
                        type="text" 
                        placeholder="Enter id"
                        value={this.state.newsinhvien.mssv}
                        onChange={this.onAddProductIdChange}
                      />
                    </div>
                    <div className="control">
                      <button type="submit" className="button is-primary is-medium">
                        Add product
                      </button>
                    </div>
                  </div>
                </form>
              </div>
              
            </div>
            <div className="column is-two-thirds">
                <div className="tile is-ancestor">
                  <div className="tile is-4 is-parent  is-vertical">
                    { 
                      this.state.sinhviens.map((product, index) => 
                        <Sinhvien 
                          isAdmin={true}
                          handleUpdateSinhvien={this.handleUpdateSinhvien}
                          handleDeleteProduct={this.handleDeleteProduct} 
                          name={product.productname}
                          date={product.sinhviendate}
                          sex={product.sinhviensex}
                          mssv={product.mssv}
                          key={product.mssv}
                        />)
                    }
                  </div>
                </div>
              </div>
          </div>
          
        </section>
      </Fragment>
    )
  }
}
